import React, { useState } from 'react';

import ExpensesFilter from './ExpenseFilter';
import ExpenseItem from "./ExpenseItem";
import Card from "../UI//Card";
import "./Expenses.css";

const Expenses = (props) => {
    const [filteredYear, setFilteredYear] = useState(2020);
    const old_expenses = props.expenses;
    const getInputtedYear = year => {
        console.log("In Expense.Js");
        console.log(year);
        setFilteredYear(year);
    }
    const expenses = []
    old_expenses.reduce((filtered, values) => {
        if(values.date.toString().indexOf(filteredYear) > -1){
            expenses.push({title:values.title, amount:values.amount, date:values.date})
        }
        return filtered;
    })

    console.log(expenses);

    return (
        <div>
            <Card className="expenses">
                <ExpensesFilter selectedYear={filteredYear} onSelectYear={getInputtedYear} />
                {expenses.map(expense => (
                    <ExpenseItem 
                        key={expense.id}
                        title={expense.title} 
                        amount={expense.amount}
                        date={expense.date}
                    />
                ))}
            </Card>
        </div>
    );
}

export default Expenses;
