import React, { useState } from 'react';

import ExpensesFilter from './ExpenseFilter';
import ExpenseItem from "./ExpenseItem";
import Card from "../UI//Card";
import "./Expenses.css";

const Expenses = (props) => {
    const [filteredYear, setFilteredYear] = useState('2021');
    const old_expenses = props.expenses;

    const expenses = old_expenses.filter(expense => {
        console.log("From Array"+expense.date.getFullYear().toString())
        console.log("From Filter"+filteredYear)
        return expense.date.getFullYear().toString() === filteredYear;
    })

    const getInputtedYear = year => {
        console.log("In Expense.Js");
        console.log(year);
        setFilteredYear(year);
    }
    console.log(filteredYear);
    console.log(old_expenses);
    console.log(expenses);

    var expenseContent = <p>No Expense Found!!!</p>

    if(expenses.length > 0){
        expenseContent = expenses.map(expense => (
            <ExpenseItem 
                key={expense.id}
                title={expense.title} 
                amount={expense.amount}
                date={expense.date}
            />
        ))
    }

    return (
        <div>
            <Card className="expenses">
                <ExpensesFilter selectedYear={filteredYear} onSelectYear={getInputtedYear} />
                {expenseContent}
            </Card>
        </div>
    );
}

export default Expenses;
